	function stripMacroFilename(filename) {
		var label = filename;

		// Remove G-code file ending from name
		var match = filename.match(/(.*)\.(g|gc|gcode)$/i);
		if (match != null) {
			label = match[1];
		}

		// Users may want to index their macros, so remove starting numbers
		match = label.match(/^\d+_(.*)/);
		if (match != null) {
			label = match[1];
		}

		return label;
	}

