	function filenameValid(name) {
		if (name.indexOf('"') != -1 || name.indexOf("'") != -1) {
			// Don't allow quotes in filenames
			return false;
		}

		if (name.indexOf("/") != -1 || name.indexOf("\"") != -1) {
			// Don't allow (back)slashes either
			return false;
		}

		if (name.indexOf(":") != -1) {
			// Colons should be avoided too
			return false;
		}

		return true;
	}

