	function arraysEqual(a, b) {
		if (a != undefined && b != undefined && a.length == b.length) {
			for(var i = 0; i < a.length; i++) {
				if (a[i].constructor === Array && b[i].constructor === Array) {
					if (!arraysEqual(a[i], b[i])) {
						return false;
					}
				} else if (a[i] != b[i]) {
					return false;
				}
			}
			return true;
		}
		return false;
	}

